package daily;

public class No392 {
    // 第一次尝试：
    public boolean isSubsequence(String s, String t) {
        if (s == null) {
            return true;
        } else if (t == null) {
            return false;
        } else if (s.length() > t.length()) {
            return false;
        } else {
            int i = 0;
            int j = 0;
            int slen = s.length()-1;
            int tlen = t.length()-1;
            while(i<=slen){
                while(i<=slen && j<=tlen && s.charAt(i) == t.charAt(j)) {
                    i++;
                    j++;
                }
                while (j<=tlen && s.charAt(i) != t.charAt(j)) {
                    j++;
                }
                if (i > slen) {
                    return true;
                }
                if (j > tlen) {
                    return false;
                }
            }
            return true;
        }
    }

    // leetcode 解法
    public boolean isSubsequence1(String s, String t) {
        int i = 0;
        int j = 0;
        while (i < s.length() & j < t.length()) {
            if (s.charAt(i) == t.charAt(j)) {
                i++;
                j++;
            } else {
                j++;
            }
        }
        return i == s.length();
    }

    public static void main(String[] args) {
        No392 no392 = new No392();
        boolean ans = no392.isSubsequence("abc",
                "");
        System.out.println(ans);
    }

}
